'WITH',
'-- ============================================================',
unistr('-- \D83D\DD39 1. Opening Balance (before :P20_FROM)'),
'-- ============================================================',
'OPENING AS (',
'    SELECT',
'        CUSTOMER_NO,',
'        SUM(BILL_BEFORE) - SUM(PAY_BEFORE) AS OPENING_BAL',
'    FROM (',
'        -- Bills before from-date',
'        SELECT ',
'            CUSTOMER_NO,',
'            SUM(CASE ',
'                    WHEN BILL_DATE < TO_DATE(:P20_FROM,''YYYY-MM-DD'')',
'                    THEN BILL_AMOUNT ELSE 0 ',
'                END) AS BILL_BEFORE,',
'            0 AS PAY_BEFORE',
'        FROM PM_BILL',
'        WHERE BILL_STATUS = 1',
'        GROUP BY CUSTOMER_NO',
'',
'        UNION ALL',
'',
'        -- Payments before from-date',
'        SELECT',
'            CUSTOMER_NO,',
'            0 AS BILL_BEFORE,',
'            SUM(CASE ',
'                    WHEN PAYMENT_DATE < TO_DATE(:P20_FROM,''YYYY-MM-DD'')',
'                    THEN PAYMENT_AMOUNT ELSE 0 ',
'                END) AS PAY_BEFORE',
'        FROM PM_PAYMENT',
'        WHERE STATUS = 1',
'        GROUP BY CUSTOMER_NO',
'    ) X',
'    GROUP BY CUSTOMER_NO',
'),',
'',
'-- ============================================================',
unistr('-- \D83D\DD39 2. Bills in Date Range (DR)'),
'-- ============================================================',
'BILLS AS (',
'    SELECT',
'        B.CUSTOMER_NO,',
'        B.BILL_DATE        AS LEDGER_DATE,',
'        ''BILL''             AS TYPE,',
'        B.BILL_NO          AS REF_NO,',
'        ''Bill''             AS NARRATION,',
'        B.BILL_AMOUNT      AS DEBIT,',
'        0                  AS CREDIT',
'    FROM PM_BILL B',
'    WHERE B.BILL_STATUS = 1',
'      AND B.BILL_DATE BETWEEN TO_DATE(:P20_FROM,''YYYY-MM-DD'')',
'                          AND TO_DATE(:P20_TO,''YYYY-MM-DD'')',
'),',
'',
'-- ============================================================',
unistr('-- \D83D\DD39 3. Payments in Date Range (CR)'),
'-- ============================================================',
'PAYMENTS AS (',
'    SELECT',
'        P.CUSTOMER_NO,',
'        P.PAYMENT_DATE     AS LEDGER_DATE,',
'        ''PAYMENT''          AS TYPE,',
'        P.PAYMENT_NO       AS REF_NO,',
'        ''Payment''          AS NARRATION,',
'        0                  AS DEBIT,',
'        P.PAYMENT_AMOUNT   AS CREDIT',
'    FROM PM_PAYMENT P',
'    WHERE P.STATUS = 1',
'      AND P.PAYMENT_DATE BETWEEN TO_DATE(:P20_FROM,''YYYY-MM-DD'')',
'                              AND TO_DATE(:P20_TO,''YYYY-MM-DD'')',
'),',
'',
'-- ============================================================',
unistr('-- \D83D\DD39 4. Combine Bills + Payments'),
'-- ============================================================',
'LEDGER_RAW AS (',
'    SELECT * FROM BILLS',
'    UNION ALL',
'    SELECT * FROM PAYMENTS',
')',
'',
'-- ============================================================',
unistr('-- \D83D\DD39 5. Final Ledger with Opening + Running Balance'),
'-- ============================================================',
'SELECT',
'    C.CUSTOMER_ID,',
'    C.CUSTOMER_NAME,',
'    L.LEDGER_DATE,',
'    L.TYPE,',
'    L.REF_NO,',
'    L.NARRATION,',
'    L.DEBIT,',
'    L.CREDIT,',
'',
unistr('    -- \D83D\DD39 Opening Balance column (same for all rows of that customer)'),
'    NVL(O.OPENING_BAL, 0) AS OPENING_BALANCE,',
'',
unistr('    -- \D83D\DD39 Running Balance = Opening + cumulative (DR \2212 CR)'),
'    NVL(O.OPENING_BAL, 0)',
'      + SUM(L.DEBIT - L.CREDIT)',
'          OVER (PARTITION BY L.CUSTOMER_NO ',
'                ORDER BY L.LEDGER_DATE, L.REF_NO) AS BALANCE',
'',
'FROM LEDGER_RAW L',
'JOIN PM_CUSTOMER C ',
'  ON C.CUSTOMER_NO = L.CUSTOMER_NO',
'LEFT JOIN OPENING O ',
'  ON O.CUSTOMER_NO = L.CUSTOMER_NO',
'',
'WHERE',
'    (:P20_CUSTOMER IS NULL OR C.CUSTOMER_NO = :P20_CUSTOMER)',
'',
'ORDER BY',
'    L.LEDGER_DATE,',
'    L.REF_NO;',
''))
