,p_sequence=>30
,p_script_type=>'INSTALL'
,p_script_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'create or replace Package Pkg_Cust_Auth Is',
'Procedure Process_Login(             P_USR_NM            Varchar2',
'                                    ,p_USR_PSW           Varchar2',
'                                    ,P_USR_COM_NO     Number',
'                                    ,p_App_Id                Number);',
'Function Authenticate_User(P_USR_NM     in     Varchar2',
'                                       ,p_USR_PSW      in   Varchar2) Return Boolean;',
'End Pkg_Cust_Auth;',
'/',
'create or replace PACKAGE BODY Pkg_Cust_Auth IS',
'',
'  PROCEDURE Process_Login(P_USR_NM   VARCHAR2,',
'                          p_Usr_Psw  VARCHAR2,',
'                          P_USR_COM_NO NUMBER,',
'                          p_App_Id    NUMBER) AS',
'    v_Result BOOLEAN := FALSE;',
'  BEGIN',
'    v_Result := Authenticate_User(P_USR_NM, p_Usr_Psw);',
'    ',
'    IF v_Result = TRUE THEN',
'      -- Redirect to Page 102 (Home Page).',
'      DECLARE',
'        CURSOR user_cur IS',
'          SELECT USER_NO, USER_NAME ',
'          FROM PM_USER ',
'          WHERE UPPER(USER_NAME) = UPPER(P_USR_NM)',
'          AND USER_PWD = p_Usr_Psw;  -- Directly compare the password',
'    ',
'        v_user_no NUMBER;',
'        v_user_nm VARCHAR2(100);   ',
'      BEGIN',
'        v_user_no := 0;',
'        v_user_nm := NULL;',
'        APEX_UTIL.SET_SESSION_STATE(''G_COMPANY_NO'', 0);',
'        ',
'        OPEN user_cur;',
'        FETCH user_cur INTO v_user_no, v_user_nm;',
'        CLOSE user_cur;',
'        ',
'        APEX_UTIL.SET_SESSION_STATE(''SS_USER_NO'', v_user_no);',
'        APEX_UTIL.SET_SESSION_STATE(''V_USER_NM'', v_user_nm);',
'        APEX_UTIL.SET_SESSION_STATE(''G_COMPANY_NO'', P_USR_COM_NO);',
'      END;',
'      ',
'      Wwv_Flow_Custom_Auth_Std.Post_Login(P_USR_NM,          -- p_User_Name',
'                                          p_Usr_Psw,          -- p_Password',
'                                          v(''APP_SESSION''),   -- p_Session_Id',
'                                          p_App_Id || '':1'');  -- p_Flow_page',
'                                                                                            ',
'    ELSE',
'      -- Login Failure, redirect to page 101 (Login Page).',
'      Owa_Util.Redirect_Url(''f?p=:101:'');',
'    END IF;',
'  END;',
'',
'  FUNCTION Authenticate_User(P_USR_NM IN VARCHAR2,',
'                             p_Usr_Psw IN VARCHAR2) RETURN BOOLEAN AS',
'    v_usr_check VARCHAR2(1);',
'  BEGIN',
'    BEGIN',
'      SELECT ''Y'' INTO v_usr_check',
'      FROM PM_USER',
'      WHERE UPPER(USER_NAME) = UPPER(P_USR_NM)',
'      AND USER_PWD = p_Usr_Psw;  -- Directly compare the password',
'    END;',
'    ',
'    IF v_usr_check = ''Y'' THEN',
'      RETURN TRUE;',
'    ELSE',
'      RETURN FALSE;',
'    END IF;',
'  END;',
'',
'END Pkg_Cust_Auth;',
'/ '))
);
