begin
wwv_flow_imp_shared.create_install_script(
 p_id=>wwv_flow_imp.id(124388886867650271720)
,p_install_id=>wwv_flow_imp.id(124180109575501390763)
,p_name=>'Alter Table'
,p_sequence=>50
,p_script_type=>'INSTALL'
,p_script_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'    PROCEDURE make_auto(p_table VARCHAR2, p_col VARCHAR2) IS',
'        v_sql       VARCHAR2(4000);',
'        v_seq       VARCHAR2(200);',
'        v_exists    NUMBER;',
'        v_start     NUMBER;',
'    BEGIN',
'        v_seq := p_table || ''_'' || p_col || ''_SEQ'';',
'',
'        -- Check max existing value',
'        BEGIN',
'            EXECUTE IMMEDIATE ''',
'                SELECT NVL(MAX(''||p_col||''),0) FROM ''||p_table',
'                INTO v_start;',
'        EXCEPTION',
'            WHEN OTHERS THEN',
'                v_start := 0;',
'        END;',
'',
'        IF v_start < 9000 THEN',
'           v_start := 9000; -- so next becomes 9001',
'        END IF;',
'',
'        -- Check sequence exists',
'        SELECT COUNT(*)',
'        INTO v_exists',
'        FROM user_sequences',
'        WHERE sequence_name = UPPER(v_seq);',
'',
'        -- Drop old sequence if exists (optional but safe)',
'        IF v_exists = 1 THEN',
'            EXECUTE IMMEDIATE ''DROP SEQUENCE ''||v_seq;',
'        END IF;',
'',
'        -- Create new sequence starting after max or 9000',
'        EXECUTE IMMEDIATE ''',
'            CREATE SEQUENCE ''||v_seq||''',
'            START WITH ''||(v_start+1)||''',
'            INCREMENT BY 1',
'            CACHE 20',
'        '';',
'',
'        -- Attach auto increment',
'        EXECUTE IMMEDIATE ''',
'            ALTER TABLE ''||p_table||''',
'            MODIFY ''||p_col||'' DEFAULT ON NULL ''||v_seq||''.NEXTVAL'';',
'    END;',
'BEGIN',
'    make_auto(''PM_AGR_MST'', ''AGR_NO'');',
'    make_auto(''PM_AGR_DTLS'', ''AGRDTL_NO'');',
'',
'    make_auto(''PM_BILL'', ''BILL_NO'');',
'    make_auto(''PM_PAYMENT'', ''PAYMENT_NO'');',
'',
'    make_auto(''PM_CUSTOMER'', ''CUSTOMER_NO'');',
'    make_auto(''PM_CUSTTYPE'', ''CUSTTYPE_NO'');',
'',
'    make_auto(''PM_ITEM'', ''ITEM_NO'');',
'    make_auto(''PM_UOM'', ''UOM_NO'');',
'    make_auto(''PM_OWNER'', ''OWNER_NO'');',
'    make_auto(''PM_TENANT_OWNERS'', ''TENANT_OWNER_NO'');',
'',
'    make_auto(''PM_SALESMANAGER'', ''MANAGER_NO'');',
'',
'    make_auto(''PM_MENU'', ''MENU_NO'');',
'    make_auto(''PM_USER_ROLE'', ''ROLE_NO'');',
'    make_auto(''PM_USER_ROLE_DTL'', ''ROLEDTL_NO'');',
'    make_auto(''PM_USER_ROLE_MAP'', ''MAP_NO'');',
'    make_auto(''PM_USER'', ''USER_NO'');',
'END;',
'/',
''))
);
